#!/usr/bin/env node

const fs = require('fs');
const path = require('path');

// Create a minimal 1024x1024 PNG with blue background and white "SE" text
// This is a base64-encoded PNG image
const iconBase64 = ``;

// Save the icon
const assetsDir = path.join(__dirname, '..', 'assets');
const iconPath = path.join(assetsDir, 'icon.png');

// Create assets directory if it doesn't exist
if (!fs.existsSync(assetsDir)) {
  fs.mkdirSync(assetsDir, { recursive: true });
}

// Write the PNG file
const buffer = Buffer.from(iconBase64, 'base64');
fs.writeFileSync(iconPath, buffer);

console.log(`Icon created at: ${iconPath}`);
console.log('Note: This is a placeholder icon. Replace it with your custom icon later.');
